pkg load statisticsrand ("seed", 1234)% numero de muestrasN = 10000;% desvio estandar realsigma = 1.5;% vector aleatorio normal N(1,sigma)X = 1+ sigma * randn(N,1);% ordeno el vectorX_ord = sort(X);% porcentaje de recorte, simetricop = 0.05 / 2;% me quedo con el 95% de las muestras para despues estimar el rangoX_recortado = X_ord(round(p*N):round((1-p)*N));% estimo el rango como max-min del vector recortadorango = X_recortado(length(X_recortado)) - X_recortado(1)s_muestral = rango/4sigma_real = sigma