pkg load statisticsrandn ("seed", 1234)% numero de muestrasN = 10000;% percentil de recorte (por debajo y por arriba)p = 0.1;% vector aleatorio normal N(1,1)X = 1 + randn(N,1);% media recortada de XX_recortado_media_normal= mediaRecortada(X,p)% comparo con media sin recortarX_media_normal =  mean(X)%  hago lo mismo pero con la distribucion exponencial (asimetrica)% defino el parametro lambdalambda = 1;% genero las muestras exponenciales de parametro lambdaX = exprnd(lambda, N,1);% media recortada de XX_recortado_media_exponencial= mediaRecortada(X,p)% comparo con media sin recortarX_media_exponencial =  mean(X)