pkg load statistics% numero de tiradasN = 5000;% p. de cara%p1 = 0.005;p1 = 0.5;p2 = 0.7;% cara = 1, ceca =0% simulo N tiradas de un dado con probabilidad de cara p1dado1 =  binornd(1,p1,N,1);% simulo N tiradas de un dado con probabilidad de cara p2dado2 =  binornd(1,p2,N,1);%dado2 =  dado1;% estimo p1, p2 - p. de carap1_cara_est  = sum(dado1)/Np2_cara_est  = sum(dado2)/N% estimo p. de cecap1_ceca_est = 1-p1_cara_est;p2_ceca_est = 1-p2_cara_est;% entropias teoricas% entropia dado 1entropia1 = entropia([p1 (1-p1)])% entropia dado 2entropia2 = entropia([p2 (1-p2)])% estimo entropias% entropia dado 1entropia_est1 = entropia([p1_cara_est  p1_ceca_est])% entropia dado 2entropia_est2 = entropia([p2_cara_est  p2_ceca_est])% estimo divergencias KLdivergenciaKL1 = divergenciaKL([p1      (1-p1)], [p1_cara_est      p1_ceca_est])divergenciaKL2 = divergenciaKL([p2      (1-p2)], [p2_cara_est      p2_ceca_est])% estimo entropia cruzadaentropiaCruzada1 = entropiaCruzada([p1      (1-p1)], [p1_cara_est      p1_ceca_est])entropiaCruzada2 = entropiaCruzada([p2      (1-p2)], [p2_cara_est      p2_ceca_est])% estimo informacion mutua% X: caras en dado 1,   cecas en dado 1% Y: cara en dado 2,   cecas en dado 2p_x= [p1_cara_est   (1-p1_cara_est)];p_y= [p2_cara_est   (1-p2_cara_est)];% p. cara en dado1 y cara en dado2 simultaneamentep_xy =[sum((dado1==1 & dado2==1))/N             sum((dado1==0 & dado2==1))/N ;                      sum((dado1==1 & dado2==0))/N             sum((dado1==0 & dado2==0))/N]; % informacion mutuainformacionMutua12 = informacionMutua(p_xy, p_x ,p_y)