pkg load statistics% tabla de datos% col 1: goles selec. nacional% col 2: goles en equipos% fila 1: goles de Maradona% fila 2: goles de Messidatos = [34    320;                           71    741];% alpha testalpha  = 0.05;                          % ni                     totalFilas = sum(datos,2);% njtotalColumnas = sum(datos,1);% ntotal = sum(totalFilas);% calculo la tabla de valores esperadosvaloresEsperados = zeros(2,2);% valor esperado de goles en seleccion en caso de independencia estadistica, suma de primera fila por suma de primera columna dividido el grand total de golesvaloresEsperados(1,1) = totalFilas(1) * totalColumnas(1) / total;valoresEsperados(1,2) = totalFilas(1) * totalColumnas(2) / total;valoresEsperados(2,1) = totalFilas(2) * totalColumnas(1) / total;valoresEsperados(2,2) = totalFilas(2) * totalColumnas(2) / total;% calculo la estadistica del testT = 0;for i=1:2  for j=1:2     T = T + (datos(i,j) - valoresEsperados(i,j))^2 / valoresEsperados(i,j); end
end
% calculo el percentil de la chi cuadrado con df=(2-1)*(2-1) = 1 grados de libertad y un alpha de 0.05df = (2-1)*(2-1);% notar que el test de chi cuadrado es un test unidireccional T_limite = chi2inv(1-alpha, df);if(T>T_limite)disp('Algunos genios del futbol rinden mejor que otros en la seleccion')elsedisp('Todos los genios del futbol rinden igual de bien en la seleccion')end