pkg load statistics% vector de preciosApple diarios por 6 mesesdatosApple = csvread("AAPL.csv");datosMicrosoft = csvread("MSFT.csv");% longitud de muestras (en dias) del promediador movil de preciosx_filtro = 30;% precios Apple filtrados y desvio estandar estimado[preciosApple_filt , s_Apple, mediana_Apple, rango_Apple]= filtroDatos(datosApple, x_filtro);% precios Microsoft filtrados[preciosMicrosoft_filt, s_Microsoft, mediana_Microsoft, rango_Microsoft] = filtroDatos(datosMicrosoft, x_filtro);% genero la matriz de datos% col 1: Apple% col 2: Microsoft% fila 1: cantidad de datos% fila 2: precio de cierre promedio de la accion% fila 3: desvio estandar en las acciones% estandarizo el precio promedio respecto de la mediana% estandarizo el desvio estandar de cada muestra filtrada (sin promediar, de esto se encarga la estadistica del test) respecto de la medianacoefEstandarizApple = mediana_Apple;coefEstandarizMicrosoft = mediana_Microsoft;%coefEstandarizApple = rango_Apple;%coefEstandarizMicrosoft= rango_Microsoft;%coefEstandarizApple = min(preciosApple_filt);%coefEstandarizMicrosoft= min(preciosMicrosoft_filt);% valores iniciales luego de filtradocoefEstandarizApple = preciosApple_filt(1);coefEstandarizMicrosoft= preciosMicrosoft_filt(1);coefEstandarizApple = rango_Apple / 4;coefEstandarizMicrosoft= rango_Microsoft / 4;coefEstandarizApple = 1;coefEstandarizMicrosoft= 1;% genero la matriz de datosmatrizDatos = [length(preciosApple_filt)                                                                        length(preciosMicrosoft_filt) ;                                                                                            mean(preciosApple_filt) /coefEstandarizApple       mean(preciosMicrosoft_filt) / coefEstandarizMicrosoft ;                                                                                            s_Apple/coefEstandarizApple                                                             s_Microsoft/coefEstandarizMicrosoft ];                            % nivel de significancia del testalpha = 0.05;% genero la estadistica del test y el intervalo de confianza[IC_min  IC_max] = testMediasTdeStudent2muestras(matrizDatos, alpha)% H0: las medias son iguales (es decir la diferencia entre ellas es 0)% pregunto si el 0 esta contenido en el IC, si es asi no puedo rechazar H0if(0 >= IC_min && 0 <= IC_max)disp('Apple y Microsoft rindieron igual de bien en los ultimos 6 meses')elsedisp('Apple y Microsoft no rindieron igual de bien en los ultimos 6 meses')end% confirmacion graficaplot(preciosApple_filt/coefEstandarizApple)hold onplot(preciosMicrosoft_filt/coefEstandarizMicrosoft)hold off