pkg load statistics% tabla de datos% col 1: Maradona% col 2: Messi% col 3: Riquelme% fila 1: numero de partidos (muestras por jugador)% fila 2: goles promedio en seleccion% fila 3: desvio estandar goles en seleccion% H0: las medias son iguales (es decir la diferencia entre ellas es 0)datos = [91                                                                       142                                                                 51;                         0.37                                                                    0.5                                                                 0.33;                           sqrt(91*0.37*(1-0.37))      sqrt(142*0.5*(1-0.5))     sqrt(51*0.33*(1-0.33))];% nivel de significancia del testalpha = 0.05;% cantidad de grupo de datosk = 3;% pongo la cantidad de partidos (muestras) en un vectorn_vector = datos(1,:) ;% pongo los goles promedios en un vectorx_media_vector = datos(2,:) ;% pongo los desvios estandar en un vectors_vector = datos(3,:) ;% cantidad total de muestrasn_tot = sum(n_vector);% calculo la media total (grand mean)x_media_total = 0;for i=1:k    x_media_total = x_media_total + (1/n_tot) * n_vector(i)*x_media_vector(i);endx_media_total% grados de libertad entre gruposdf_e = k-1;% calculo la varianza cuadratica entre gruposS2_e = 0;for i=1:k    S2_e = S2_e + (1/df_e) * n_vector(i) * (x_media_vector(i) - x_media_total)^2;endS2_e% grados de libertad dentro de los gruposdf_d = n_tot - k;% calculo la varianza cuadratica dentro de los gruposS2_d = 0;for i=1:k    S2_d = S2_d + (1/df_d) * (n_vector(i) - 1)* (s_vector(i))^2;endS2_d% estadistica del testF = S2_e / S2_d% busco el valor critico del test para el alpha pedidoF_critico= finv(1-alpha, df_e, df_d)% pregunto si el 0 esta contenido en el IC, si es asi no puedo rechazar H0if(F <= F_critico)disp('Messi, Riquelme y Maradona rindieron igual de bien en la seleccion nacional')elsedisp('Messi, Riquelme y Maradona no rindieron igual de bien en la seleccion nacional')end