pkg load statistics% numero de tiradasN = 1000;% p. de carap1 = 0.5;p2 = 0.7;% cara = 1, ceca =0% simulo N tiradas de un dado con probabilidad de cara p1dado1 =  binornd(1,p1,N,1);% simulo N tiradas de un dado con probabilidad de cara p2dado2 =  binornd(1,p2,N,1);% estimo p1, p2 - p. de carap1_cara_est  = sum(dado1)/N;p2_cara_est  = sum(dado2)/N;% estimo p. de cecap1_ceca_est = 1-p1_cara_est;p2_ceca_est = 1-p2_cara_est;% estimo entropias% entropia dado 1entropia1 = entropia([p1_cara_est  p1_ceca_est])% entropia dado 2entropia2 = entropia([p2_cara_est  p2_ceca_est])% estimo divergencias KLdivergenciaKL1 = divergenciaKL([p1_cara_est      p1_ceca_est], [p1      (1-p1)])divergenciaKL2 = divergenciaKL([p2_cara_est      p2_ceca_est], [p2      (1-p2)])% estimo divergencia cruzadaentropiaCruzada1 = entropiaCruzada([p1_cara_est      p1_ceca_est], [p1      (1-p1)])entropiaCruzada2 = entropiaCruzada([p2_cara_est      p2_ceca_est], [p2      (1-p2)])% estimo informacion mutua% X: cara en dado 1,   Y: cara en dado 2p_x = p1_cara_est;p_y = p2_cara_est;% p. cara en dado1 y cara en dad2 simultaneamentep_xy =sum((dado1==1 & dado2==1))/N;% informacion mutuainformacionMutua12 = informacionMutua(p_xy, p_x, p_y)